import React from 'react';
import {Navbar} from '../Navar/Navbar';
import {Banner} from "../Banner/Banner";
import {GlobalStyle} from "../Styles/GlobalStyle";
import {FoodDialog, Dialog} from "../FoodDialog/FoodDialog";
import styled from "styled-components";
import {Title} from "../Styles/title";
import {isAuthenticated} from "../auth";
import {Link} from 'react-router-dom';
import {Food, FoodGrid, FoodLabel} from "../Menu/FoodGrid";


const AdminDashboard =() => {

    const {user} = isAuthenticated();

    const adminLinks = () =>{
        return (
            <div>
                <h1>Admin Links</h1>
                <ul>
                    <Link className="nav-link" to="/create/category">Add Category</Link>
                    <Link className="nav-link" to="/create/products">Add Product</Link>
                    <Link className="nav-link" to="/create/orders">View Orders</Link>

                </ul>
            </div>
        )
    }

    const adminInfo = () => {
        return (
        <div>
        <h3>User Information</h3>
         <ul>
          <li>Name: {user.name}</li>
          <li>Email: {user.email} </li>
          <li>Role: {user.role === 1? 'Admin' : "Registered User"}</li>
       </ul>   
        </div>
        )
    }

    return(
        <>
    <GlobalStyle/>
    <FoodDialog/>
    <Navbar/>
    <Banner/>

    <div className="row">
        <div className="col-3">
            {adminLinks()}
        </div>
        <div className="col-9">
            {adminInfo()}
        </div>
    </div>
   </>
    )
}
export default AdminDashboard;
