import {useEffect} from 'react';

export function useTitle({openFood, orders}){
    useEffect(()=>{
    if(openFood){
        document.title = openFood.name;
    } else {
        document.title = orders.lenth === 0 ? `What's for dinner` : `[${orders.lenth} items in your order!]`;
    }
});
}
