import React, {useState, useEffect} from 'react';
import styled from 'styled-components';
import {DialogContent, DialogFooter, DialogBanner, DialogBannerName, ConfirmButton} from "../FoodDialog/FoodDialog";
import {formatPrice} from "../Data/FoodData";
import {getPrice} from "../FoodDialog/FoodDialog";
import {FoodLabel} from "../Menu/FoodGrid";
import {API} from "../config";
import {getCart} from "../Cart/carthelper";

const CartStyled = styled.div`
top: 300px;
width: 600px;
background-color: white;
height: 300px;
z-index: 10;
box-shadow: 4px 0px 5px 4px grey;
display: flex;
flex-direction: column;
`;

const CartContent = styled(DialogContent)`
padding: 20px;
height: 100%
`;

const CartContainer = styled.div`
    padding: 10px 0px;
    border-bottom: 1px solid grey;
    ${({editable}) =>
editable ? `
&:hover {
    cursor: pointer;
    background-color: #e7e7e7;
}
`
    : `
    pointer-events: none;
    `}
`;

const CartItem = styled.div`
    padding: 10px 0px;
    display: grid;
    grid-template-columns: 20px 150px 20px 60px;
    justify-content: space-between;
`;
export const CartBanner = styled.div`
min-height: 50px;
margin-bottom: 20px;
background-image: url("img/shark.jpeg");
background-position: center;
background-size: cover;
`;
export const CartBannerName = styled(FoodLabel)`
    font-size: 30px;
    padding: 5px 40px;

`;

const DetailItem = styled.div`
    color: gray;
    font-size: 10px;
`

export function Cart() {
    // const subtotal = orders.reduce((total, order) => {
    //     return total + getPrice(order);
    // }, 0);
    // const tax = subtotal * 0.07;
    // const processingFee = (tax + subtotal) * 0.05;
    // const total = subtotal + tax + processingFee;
    const [items, setItems] = useState([]);
    const [run, setRun] = useState(false);

    useEffect(()=> {
        setItems(getCart());
    }, [run]);

    const showItems = items => {
        return(
            <div>
                <h2>Your cart has {`${items.length}`} items.</h2>
                <hr/>
                {items.map((product, i)=> (
                <DetailItem 
                key ={i} 
                items={product}
                setRun={setRun}
                run={run}
                />
                ))}
            </div>
        );
    };

    return <CartStyled>
    <CartContainer>
    <CartBanner img={`${API}/product/photo/shark.jpeg`}>
        <CartBannerName>Summary, your cart has {`${items.length}`} items.</CartBannerName>
    </CartBanner>
        <CartContent>
            {showItems(items)}
        </CartContent>
    </CartContainer>
    </CartStyled>
}
