import React, {useState, useEffect} from 'react';
import styled from 'styled-components';
import {formatPrice} from "../Data/FoodData";
import {API} from "../config";
import {isAuthenticated} from '../auth';
import {useOpenFood} from "../Hooks/useOpenFood";
import {useOrders} from "../Hooks/useOrders";
import {useTitle} from "../Hooks/useTitle";
import {DialogContent, DialogFooter, DialogBanner, DialogBannerName, ConfirmButton} from "../FoodDialog/FoodDialog";

export const FoodGrid = styled.div`
display: grid;
width: 1000px;
grid-template-columns: 1fr 1fr;
gap: 20px;
padding-bottom: 40px;
`;


const CartStyled = styled.div`
top: 300px;
width: 600px;
background-color: white;
height: 300px;
z-index: 10;
box-shadow: 4px 0px 5px 4px grey;
display: flex;
flex-direction: column;
`;

const CartContent = styled(DialogContent)`
padding: 20px;
height: 100%
`;

const CartContainer = styled.div`
    padding: 10px 0px;
    border-bottom: 1px solid grey;
    ${({editable}) =>
editable ? `
&:hover {
    cursor: pointer;
    background-color: #e7e7e7;
}
`
    : `
    pointer-events: none;
    `}
`;

const CartItem = styled.div`
    padding: 10px 0px;
    display: grid;
    grid-template-columns: 20px 150px 20px 60px;
    justify-content: space-between;
`;
export const CartBanner = styled.div`
min-height: 50px;
margin-bottom: 20px;
background-image: url("img/shark.jpeg");
background-position: center;
background-size: cover;
`;
export const CartBannerName = styled(FoodLabel)`
    font-size: 25px;
    padding: 5px 20px;

`;

const DetailItem = styled.div`
    color: gray;
    font-size: 10px;
`

const PaymentStyled = styled.div`
    height: 300px;
    width: 600px;
    z-index: 10;
box-shadow: 4px 0px 5px 4px grey;
display: flex;
flex-direction: column;
`
const ShippingBox = styled.div`
    height: 300px;
    width: 600px;
    z-index: 10;
box-shadow: 4px 0px 5px 4px grey;
display: flex;
flex-direction: column;
`


export function CheckoutStyled(){ 
    const openFood = useOpenFood();
    const orders = useOrders();
    useTitle({...openFood, ...orders});
    const {user, token} = isAuthenticated();
    const [items, setItems] = useState([]);
    const [run, setRun] = useState(false);
    const [finalOrder, setFinalOrder] = useState(
        {
    user: "",
    orderItems: "",
    ShipName: "",
    ShipEmail: "",
    ShipAddress: "",
    ShipCity: "",
    ShipState: "",
    ShipZip: "",
    method: "",
    payment: "",
    subTotal: "",
    taxPrice: "",
    processingFee:"",
    totalPrice: "",
        }
    );

    useEffect(()=> {
        setItems(getCart());
    }, [run]);

    const showItems = items => {
        return(
            <div>
                <hr/>
                {items.map((product, i)=> (
                <DetailItem 
                key ={i} 
                items={product}
                setRun={setRun}
                run={run}
                >{product.name} {" Qty: "} {product.quantity} {" "} {product.topping}{" $"}{product.price}</DetailItem>
                ))
                }
            </div>
        );
    };
    const [values, setValues] = useState({
        shipname: '',
        email: '',
        address:'',
        city:'',
        state: '',
        zip: '',
        method: ''
    })

    const {
    shipname,
    email,
    address,
    city,
    state,
    zip,
    method
    } = values;

    const handleChange = name => event => {
        const value = name === 'name' ? event.target.files[0]: event.target.value;
        setValues({...values, [name]: value});
    };

    const clickSubmit = event => {
        event.preventDefault();
        setValues({...values});
        console.log((values));
     };

     const handleMethod = event => {
        setValues({...values, method: event.target.value });
        console.log(values);
    };

    let methodSelection = values.method;

    const shippingMethod = () => (
        <div>
                        <label>Method Type:</label>
                        <select 
            className="form-control" onChange={handleMethod}
           >
                <option>Select Method</option>
                <option>Pick Up</option>
                <option>Delivery</option>
            </select>
       </div>
    )

    const shippingForm = () => (
        <form className="mb-3" onSubmit={clickSubmit}>
            <div className="form-group">
                <label className="text-muted">Name: </label>
                <input onChange={handleChange('shipname')} type="text" className="form-control" value={shipname}/>
            </div>
            <div className="form-group">
                <label className="text-muted">Email: </label>
                <input onChange={handleChange('email')} type="text" className="form-control" value={email}/>
            </div>
            <div className="form-group">
                <label className="text-muted">Address: </label>
                <input onChange={handleChange('address')} type="text" className="form-control" value={address}/>
            </div>
            <div className="form-group">
                <label className="text-muted">City: </label>
                <input onChange={handleChange('city')} type="text" className="form-control" value={city}/>
            </div>
            <div className="form-group">
                <label className="text-muted">State: </label>
                <input onChange={handleChange('state')} type="text" className="form-control" value={state}/>
            </div>
            <div className="form-group">
                <label className="text-muted">Zip: </label>
                <input onChange={handleChange('zip')} type="text" className="form-control" value={zip}/>
            </div>
            <button className="btn btn-outline-primary">Shipping</button>
        </form>
    );

    return (
        <>
        <FoodGrid> 
    <ShippingBox>
        <CartBanner>
            <CartBannerName>Delivery Address</CartBannerName></CartBanner>
        {shippingMethod()}
        {shippingForm()}
    </ShippingBox>

    <CartStyled>
    <CartContainer>
    <CartBanner img={`${API}/product/photo/shark.jpeg`}>
        <CartBannerName>Summary, your cart has {`${items.length}`} items.</CartBannerName>
    </CartBanner>
        <CartContent>
            {showItems(items)}
        </CartContent>
    </CartContainer>
    </CartStyled>

    <PaymentStyled>
    <CartBanner>
            <CartBannerName>Payment Method</CartBannerName></CartBanner>
    </PaymentStyled>
    </FoodGrid>
    </>
    );
}
